import React, { Component } from 'react';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import * as faSVG from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Container,
} from 'react-bootstrap';

import IconContainer from './IconContainer';

const iconList = Object.keys(faSVG);
iconList.splice(0, 2);

class App extends Component {
  constructor () {
    super();
    this.state = {
      searchKeyword: '',
      faList: iconList,
    }
  }

  searchIcon(searchKeyword) {
    this.setState({searchKeyword});
    if (searchKeyword.length > 0) {
      this.setState({faList: iconList.filter(icon => {
        return icon.slice(2).toLowerCase().includes(searchKeyword.toLowerCase());
      })});
    }
  }

  render() {
    return (
      <div className="App">
        <Container>
        <div className="input-group mt-5 mb-4 pr-4">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1"><FontAwesomeIcon icon={faSVG['faSearch']} /></span>
          </div>
          <input
            type="text"
            className="form-control"
            placeholder="Search Icon"
            value={this.state.searchKeyword}
            onChange={(e) => this.searchIcon(e.target.value)}
          />
        </div>
        {/* Show the search re-rendering performance diff without PureComponent */}
        <IconContainer
          iconList={this.state.faList}
        />
        </Container>
      </div>
    );
  }
}

export default App;
